mapper.select:
SELECT A.* IFNULL(M.name, "삭제된_회원") AS extra__writerName, IFNULL(M.name, "삭제된_회원") AS extra__writerRealName FROM article AS A LEFT JOIN member AS M ON A.memberId = M.id WHERE A.boardId = #{boardId}
AND ( A.title LIKE CONCAT('%', #{keyword}, '%') OR A.content LIKE CONCAT('%', #{keyword}, '%') )
AND A.title LIKE CONCAT('%', #{keyword}, '%')
AND A.content LIKE CONCAT('%', #{keyword}, '%')
ORDER BY A.id DESC LIMIT #{limitFrom}, #{limitTake}

mapper.update:
UPDATE article
SET updateDate = NOW(),
title = #{title},
content = #{content}
WHERE id = #{id}
